var components=require("../components.js");function loadLanguages(arr){if(!arr){arr=Object.keys(components.languages).filter(function(lang){return"meta"!==lang})}if(!Array.isArray(arr)){arr=[arr]}arr.forEach(function(language){if(components.languages[language]&&components.languages[language].require){loadLanguages(components.languages[language].require)}require("./prism-"+language)})}module.exports=loadLanguages;