var sauceBrowsers=require("./sauce_browsers.json");function getBrowserName(browserCap){var name="internet explorer"==browserCap.browserName?"ie":browserCap.browserName,version=browserCap.version||"-latest";return name+version}function getJobName(browserCap){var browserName=getBrowserName(browserCap);return"false"==process.env.TRAVIS_PULL_REQUEST?"CO-"+process.env.TRAVIS_BRANCH+"-"+browserName:"PR-"+process.env.TRAVIS_PULL_REQUEST+"-"+browserName+"-"+process.env.TRAVIS_BRANCH}function getBrowserCapabilities(browsers){for(var caps=[],i=0,b;i<browsers.length;i++){b=browsers[i];b["tunnel-identifier"]=process.env.TRAVIS_JOB_NUMBER;b.build=process.env.TRAVIS_BUILD_NUMBER;b.name=getJobName(b);caps.push(b)}return caps}module.exports=getBrowserCapabilities(sauceBrowsers);