goog.provide("goog.async.ThrottleTest");goog.setTestOnly("goog.async.ThrottleTest");goog.require("goog.async.Throttle");goog.require("goog.testing.MockClock");goog.require("goog.testing.jsunit");function testThrottle(){var clock=new goog.testing.MockClock(!0),callBackCount=0,callBackFunction=function(){callBackCount++},throttle=new goog.async.Throttle(callBackFunction,100);assertEquals(0,callBackCount);throttle.fire();assertEquals(1,callBackCount);throttle.fire();assertEquals(1,callBackCount);throttle.fire();throttle.fire();assertEquals(1,callBackCount);clock.tick(101);assertEquals(2,callBackCount);clock.tick(101);assertEquals(2,callBackCount);throttle.fire();assertEquals(3,callBackCount);throttle.fire();assertEquals(3,callBackCount);throttle.stop();clock.tick(101);assertEquals(3,callBackCount);throttle.fire();assertEquals(4,callBackCount);clock.tick(101);assertEquals(4,callBackCount);throttle.fire();throttle.fire();assertEquals(5,callBackCount);throttle.pause();throttle.resume();assertEquals(5,callBackCount);throttle.pause();clock.tick(101);assertEquals(5,callBackCount);throttle.resume();assertEquals(6,callBackCount);clock.tick(101);assertEquals(6,callBackCount);throttle.pause();throttle.fire();assertEquals(6,callBackCount);clock.tick(101);assertEquals(6,callBackCount);throttle.resume();assertEquals(7,callBackCount);throttle.pause();throttle.pause();clock.tick(101);throttle.fire();throttle.resume();assertEquals(7,callBackCount);throttle.resume();assertEquals(8,callBackCount);throttle.pause();throttle.pause();throttle.fire();throttle.resume();clock.tick(101);assertEquals(8,callBackCount);throttle.resume();assertEquals(9,callBackCount);clock.uninstall()}function testThrottleScopeBinding(){var interval=500,mockClock=new goog.testing.MockClock(!0),x={y:0};new goog.async.Throttle(function(){++this.y},interval,x).fire();assertEquals(1,x.y);mockClock.uninstall()}function testThrottleArgumentBinding(){var interval=500,mockClock=new goog.testing.MockClock(!0),calls=0,throttle=new goog.async.Throttle(function(a,b,c){++calls;assertEquals(3,a);assertEquals("string",b);assertEquals(!1,c)},interval);throttle.fire(3,"string",!1);assertEquals(1,calls);throttle.fire();mockClock.tick(interval/2);throttle.fire(8,null,!0);throttle.fire(3,"string",!1);mockClock.tick(interval);assertEquals(2,calls);mockClock.uninstall()}function testThrottleArgumentAndScopeBinding(){var interval=500,mockClock=new goog.testing.MockClock(!0),x={calls:0},throttle=new goog.async.Throttle(function(a,b,c){++this.calls;assertEquals(3,a);assertEquals("string",b);assertEquals(!1,c)},interval,x);throttle.fire(3,"string",!1);assertEquals(1,x.calls);throttle.fire();mockClock.tick(interval/2);throttle.fire(8,null,!0);throttle.fire(3,"string",!1);mockClock.tick(interval);assertEquals(2,x.calls);mockClock.uninstall()}