goog.provide("goog.crypt.Arc4Test");goog.setTestOnly("goog.crypt.Arc4Test");goog.require("goog.array");goog.require("goog.crypt.Arc4");goog.require("goog.testing.jsunit");function testEncryptionDecryption(){var key=[37,38,39,40],startArray=[97,98,99,100,101,102,103],byteArray=[97,98,99,100,101,102,103],arc4=new goog.crypt.Arc4;arc4.setKey(key);arc4.crypt(byteArray);assertArrayEquals(byteArray,[81,187,221,149,155,66,52]);arc4=new goog.crypt.Arc4;arc4.setKey(key);arc4.crypt(byteArray);assertArrayEquals(byteArray,startArray)}function testDiscard(){var key=[37,38,39,40],data=[97,98,99,100,101,102,103],arc4=new goog.crypt.Arc4;arc4.setKey(key);arc4.discard(256);var withDiscard=goog.array.clone(data);arc4.crypt(withDiscard);arc4=new goog.crypt.Arc4;arc4.setKey(key);var withCrypt=goog.array.clone(data);arc4.crypt(Array(256));arc4.crypt(withCrypt);assertArrayEquals(withDiscard,withCrypt)}