goog.provide("goog.crypt.Sha1Test");goog.setTestOnly("goog.crypt.Sha1Test");goog.require("goog.crypt");goog.require("goog.crypt.Sha1");goog.require("goog.crypt.hashTester");goog.require("goog.testing.jsunit");goog.require("goog.userAgent");function testBasicOperations(){var sha1=new goog.crypt.Sha1;goog.crypt.hashTester.runBasicTests(sha1)}function testBlockOperations(){var sha1=new goog.crypt.Sha1;goog.crypt.hashTester.runBlockTests(sha1,64)}function testHashing(){var sha1=new goog.crypt.Sha1;assertEquals("da39a3ee5e6b4b0d3255bfef95601890afd80709",goog.crypt.byteArrayToHex(sha1.digest()));sha1.reset();sha1.update([97,98,99]);assertEquals("a9993e364706816aba3e25717850c26c9cd0d89d",goog.crypt.byteArrayToHex(sha1.digest()));sha1.reset();sha1.update("abcdbcdecdefdefgefghfghighijhijkijkljklmklmnlmnomnopnopq");assertEquals("84983e441c3bd26ebaae4aa1f95129e5e54670f1",goog.crypt.byteArrayToHex(sha1.digest()));if(!goog.userAgent.IE||goog.userAgent.isVersionOrHigher("9")){for(var thousandAs=[],i=0;1e3>i;++i){thousandAs[i]=97}sha1.reset();for(var i=0;1e3>i;++i){sha1.update(thousandAs)}assertEquals("34aa973cd4c4daa4f61eeb2bdbad27316534016f",goog.crypt.byteArrayToHex(sha1.digest()))}sha1.reset();sha1.update("The quick brown fox jumps over the lazy dog");assertEquals("2fd4e1c67a2d28fced849ee1bb76e7391b93eb12",goog.crypt.byteArrayToHex(sha1.digest()))}