goog.provide("goog.crypt.Sha224Test");goog.setTestOnly("goog.crypt.Sha224Test");goog.require("goog.crypt");goog.require("goog.crypt.Sha224");goog.require("goog.crypt.hashTester");goog.require("goog.testing.jsunit");function testBasicOperations(){var sha224=new goog.crypt.Sha224;goog.crypt.hashTester.runBasicTests(sha224)}function testHashing(){var sha224=new goog.crypt.Sha224;sha224.update(goog.crypt.stringToByteArray("abc"));assertEquals("23097d223405d8228642a477bda255b32aadbce4bda0b3f7e36c9da7",goog.crypt.byteArrayToHex(sha224.digest()));sha224.reset();sha224.update(goog.crypt.stringToByteArray("abcdbcdecdefdefgefghfghighij"+"hijkijkljklmklmnlmnomnopnopq"));assertEquals("75388b16512776cc5dba5da1fd890150b0c6455cb4f58b1952522525",goog.crypt.byteArrayToHex(sha224.digest()));sha224.reset();var biggerThanOneBlock="abcdbcdecdefdefgefghfghighij"+"hijkijkljklmklmnlmnomnopnopq"+"asdfljhr78yasdfljh45opa78sdf"+"120839414104897aavnasdfafasd";assertTrue(biggerThanOneBlock.length>goog.crypt.Sha2.BLOCKSIZE_&&biggerThanOneBlock.length<2*goog.crypt.Sha2.BLOCKSIZE_);sha224.update(goog.crypt.stringToByteArray(biggerThanOneBlock));assertEquals("27c9b678012becd6891bac653f355b2d26f63132e840644d565f5dac",goog.crypt.byteArrayToHex(sha224.digest()));sha224.reset();var biggerThanTwoBlocks="abcdbcdecdefdefgefghfghighij"+"hijkijkljklmklmnlmnomnopnopq"+"asdfljhr78yasdfljh45opa78sdf"+"120839414104897aavnasdfafasd"+"laasdouvhalacbnalalseryalcla";assertTrue(biggerThanTwoBlocks.length>2*goog.crypt.Sha2.BLOCKSIZE_);sha224.update(goog.crypt.stringToByteArray(biggerThanTwoBlocks));assertEquals("1c2c1455cc984eef6f25ec9d79b1c661b3794887c3d0b24111ed9803",goog.crypt.byteArrayToHex(sha224.digest()))}