goog.provide("goog.ds.JsXmlHttpDataSourceTest");goog.setTestOnly("goog.ds.JsXmlHttpDataSourceTest");goog.require("goog.ds.JsXmlHttpDataSource");goog.require("goog.testing.TestQueue");goog.require("goog.testing.jsunit");goog.require("goog.testing.net.XhrIo");var TEXT_PREFIX=null,TEXT_POSTFIX=null,INDEX_OF_URI_ENTRY=1,INDEX_OF_CONTENT_ENTRY=3;function setUp(){}function tearDown(){}function testLoad_WithPostAndQueryDataSet(){var USE_POST=!0,dataSource=new goog.ds.JsXmlHttpDataSource("uri","namne",TEXT_PREFIX,TEXT_POSTFIX,USE_POST),testQueue=new goog.testing.TestQueue;dataSource.xhr_=new goog.testing.net.XhrIo(testQueue);var expectedContent="Some test content";dataSource.setQueryData(expectedContent);dataSource.load();assertFalse(testQueue.isEmpty());var actualRequest=testQueue.dequeue();assertEquals(expectedContent,actualRequest[INDEX_OF_CONTENT_ENTRY]);assertTrue(testQueue.isEmpty())}function testLoad_WithPostAndNoQueryDataSet(){var USE_POST=!0,dataSource=new goog.ds.JsXmlHttpDataSource("uri?a=1&b=2","namne",TEXT_PREFIX,TEXT_POSTFIX,USE_POST),testQueue=new goog.testing.TestQueue;dataSource.xhr_=new goog.testing.net.XhrIo(testQueue);dataSource.load();assertFalse(testQueue.isEmpty());var actualRequest=testQueue.dequeue();assertEquals("a=1&b=2",actualRequest[INDEX_OF_CONTENT_ENTRY].toString());assertTrue(testQueue.isEmpty())}function testLoad_WithGet(){var USE_GET=!1,expectedUri="uri?a=1&b=2",dataSource=new goog.ds.JsXmlHttpDataSource(expectedUri,"namne",TEXT_PREFIX,TEXT_POSTFIX,USE_GET),testQueue=new goog.testing.TestQueue;dataSource.xhr_=new goog.testing.net.XhrIo(testQueue);dataSource.load();assertFalse(testQueue.isEmpty());var actualRequest=testQueue.dequeue();assertEquals(expectedUri,actualRequest[INDEX_OF_URI_ENTRY].toString());assertTrue(testQueue.isEmpty())}