goog.LOCALE="en_US";goog.provide("goog.date.durationTest");goog.setTestOnly("goog.date.durationTest");goog.require("goog.date.duration");goog.require("goog.i18n.DateTimeFormat");goog.require("goog.i18n.DateTimeSymbols");goog.require("goog.i18n.DateTimeSymbols_bn");goog.require("goog.i18n.DateTimeSymbols_en");goog.require("goog.i18n.DateTimeSymbols_fa");goog.require("goog.testing.jsunit");var MINUTE_MS=6e4,HOUR_MS=60*MINUTE_MS,DAY_MS=24*HOUR_MS,duration=goog.date.duration.format;function testFormatDurationZeroMinutes(){assertEquals("0 minutes",duration(0));assertEquals("0 minutes",duration(MINUTE_MS-1))}function testFormatDurationMinutes(){assertEquals("1 minute",duration(MINUTE_MS));assertEquals("1 minute",duration(MINUTE_MS+1));assertEquals("5 minutes",duration(5*MINUTE_MS));assertEquals("45 minutes",duration(45*MINUTE_MS))}function testFormatDurationHours(){assertEquals("1 hour",duration(HOUR_MS));assertEquals("1 hour",duration(HOUR_MS+1));assertEquals("1 hour 1 minute",duration(HOUR_MS+MINUTE_MS));assertEquals("1 hour 45 minutes",duration(HOUR_MS+45*MINUTE_MS));assertEquals("5 hours",duration(5*HOUR_MS));assertEquals("5 hours",duration(5*HOUR_MS+1));assertEquals("5 hours 1 minute",duration(5*HOUR_MS+MINUTE_MS));assertEquals("5 hours 45 minutes",duration(5*HOUR_MS+45*MINUTE_MS));assertEquals("11 hours",duration(11*HOUR_MS));assertEquals("11 hours",duration(11*HOUR_MS+1));assertEquals("11 hours 1 minute",duration(11*HOUR_MS+MINUTE_MS));assertEquals("11 hours 45 minutes",duration(11*HOUR_MS+45*MINUTE_MS))}function testFormatDurationDays(){assertEquals("1 day",duration(DAY_MS));assertEquals("1 day",duration(DAY_MS+1));assertEquals("1 day 1 minute",duration(DAY_MS+MINUTE_MS));assertEquals("1 day 45 minutes",duration(DAY_MS+45*MINUTE_MS));assertEquals("1 day 1 hour",duration(DAY_MS+HOUR_MS));assertEquals("1 day 11 hours",duration(DAY_MS+11*HOUR_MS));assertEquals("1 day 1 hour 1 minute",duration(DAY_MS+HOUR_MS+MINUTE_MS));assertEquals("1 day 1 hour 45 minutes",duration(DAY_MS+HOUR_MS+45*MINUTE_MS));assertEquals("1 day 11 hours 1 minute",duration(DAY_MS+11*HOUR_MS+MINUTE_MS));assertEquals("1 day 11 hours 45 minutes",duration(DAY_MS+11*HOUR_MS+45*MINUTE_MS));assertEquals("11 days",duration(11*DAY_MS));assertEquals("11 days",duration(11*DAY_MS+1));assertEquals("11 days 1 minute",duration(11*DAY_MS+MINUTE_MS));assertEquals("11 days 45 minutes",duration(11*DAY_MS+45*MINUTE_MS));assertEquals("11 days 1 hour",duration(11*DAY_MS+HOUR_MS));assertEquals("11 days 11 hours",duration(11*DAY_MS+11*HOUR_MS));assertEquals("11 days 1 hour 1 minute",duration(11*DAY_MS+HOUR_MS+MINUTE_MS));assertEquals("11 days 1 hour 45 minutes",duration(11*DAY_MS+HOUR_MS+45*MINUTE_MS));assertEquals("11 days 11 hours 1 minute",duration(11*DAY_MS+11*HOUR_MS+MINUTE_MS));assertEquals("11 days 11 hours 45 minutes",duration(11*DAY_MS+11*HOUR_MS+45*MINUTE_MS))}function testFormatDurationPersianDigits(){goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_fa;assertEquals(localizeNumber(1)+" minute",duration(MINUTE_MS));assertEquals(localizeNumber(2)+" minutes",duration(2*MINUTE_MS));assertEquals(localizeNumber(10)+" hours",duration(10*HOUR_MS));assertEquals(localizeNumber(23)+" days",duration(23*DAY_MS));goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_en}function testFormatDurationBengaliDigits(){goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_bn;assertEquals(localizeNumber(1)+" minute",duration(MINUTE_MS));assertEquals(localizeNumber(2)+" minutes",duration(2*MINUTE_MS));assertEquals(localizeNumber(10)+" hours",duration(10*HOUR_MS));assertEquals(localizeNumber(23)+" days",duration(23*DAY_MS));goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_en}function localizeNumber(value){return goog.i18n.DateTimeFormat.localizeNumbers(value)}