goog.provide("goog.date.relativeWithPluralsTest");goog.setTestOnly("goog.date.relativeWithPluralsTest");goog.require("goog.date.relative");goog.require("goog.date.relativeTest");goog.require("goog.date.relativeWithPlurals");goog.require("goog.i18n.DateTimeFormat");goog.require("goog.i18n.DateTimeSymbols");goog.require("goog.i18n.DateTimeSymbols_bn");goog.require("goog.i18n.DateTimeSymbols_en");goog.require("goog.i18n.DateTimeSymbols_fa");goog.require("goog.i18n.NumberFormatSymbols");goog.require("goog.i18n.NumberFormatSymbols_bn");goog.require("goog.i18n.NumberFormatSymbols_en");goog.require("goog.i18n.NumberFormatSymbols_fa");function tearDown(){goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_en;goog.i18n.NumberFormatSymbols=goog.i18n.NumberFormatSymbols_en}function testFormatRelativeForPastDatesPersianDigits(){goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_fa;goog.i18n.NumberFormatSymbols=goog.i18n.NumberFormatSymbols_fa;var fn=goog.date.relative.format;assertEquals("Should round seconds to the minute below",localizeNumber(0)+" minutes ago",fn(timestamp("23 March 2009 14:30:10")));assertEquals("Should round seconds to the minute below",localizeNumber(1)+" minute ago",fn(timestamp("23 March 2009 14:29:56")));assertEquals("Should round seconds to the minute below",localizeNumber(2)+" minutes ago",fn(timestamp("23 March 2009 14:29:00")));assertEquals(localizeNumber(10)+" minutes ago",fn(timestamp("23 March 2009 14:20:10")));assertEquals(localizeNumber(59)+" minutes ago",fn(timestamp("23 March 2009 13:31:42")));assertEquals(localizeNumber(2)+" hours ago",fn(timestamp("23 March 2009 12:20:56")));assertEquals(localizeNumber(23)+" hours ago",fn(timestamp("22 March 2009 15:30:56")));assertEquals(localizeNumber(1)+" day ago",fn(timestamp("22 March 2009 12:11:04")));assertEquals(localizeNumber(1)+" day ago",fn(timestamp("22 March 2009 00:00:00")));assertEquals(localizeNumber(2)+" days ago",fn(timestamp("21 March 2009 23:59:59")));assertEquals(localizeNumber(2)+" days ago",fn(timestamp("21 March 2009 10:30:56")));assertEquals(localizeNumber(2)+" days ago",fn(timestamp("21 March 2009 00:00:00")));assertEquals(localizeNumber(3)+" days ago",fn(timestamp("20 March 2009 23:59:59")))}function testFormatRelativeForFutureDatesBengaliDigits(){var fn=goog.date.relative.format;goog.i18n.DateTimeSymbols=goog.i18n.DateTimeSymbols_bn;goog.i18n.NumberFormatSymbols=goog.i18n.NumberFormatSymbols_bn;assertEquals("Should round seconds to the minute below","in "+localizeNumber(1)+" minute",fn(timestamp("23 March 2009 14:32:05")));assertEquals("Should round seconds to the minute below","in "+localizeNumber(2)+" minutes",fn(timestamp("23 March 2009 14:33:00")));assertEquals("in "+localizeNumber(10)+" minutes",fn(timestamp("23 March 2009 14:40:10")));assertEquals("in "+localizeNumber(59)+" minutes",fn(timestamp("23 March 2009 15:29:15")));assertEquals("in "+localizeNumber(2)+" hours",fn(timestamp("23 March 2009 17:20:56")));assertEquals("in "+localizeNumber(23)+" hours",fn(timestamp("24 March 2009 13:30:56")));assertEquals("in "+localizeNumber(1)+" day",fn(timestamp("24 March 2009 14:31:07")));assertEquals("in "+localizeNumber(1)+" day",fn(timestamp("24 March 2009 16:11:04")));assertEquals("in "+localizeNumber(1)+" day",fn(timestamp("24 March 2009 23:59:59")));assertEquals("in "+localizeNumber(2)+" days",fn(timestamp("25 March 2009 00:00:00")));assertEquals("in "+localizeNumber(2)+" days",fn(timestamp("25 March 2009 10:30:56")));assertEquals("in "+localizeNumber(2)+" days",fn(timestamp("25 March 2009 23:59:59")));assertEquals("in "+localizeNumber(3)+" days",fn(timestamp("26 March 2009 00:00:00")))}function localizeNumber(value){return goog.i18n.DateTimeFormat.localizeNumbers(value)}