goog.provide("goog.debug.FormatterTest");goog.setTestOnly("goog.debug.FormatterTest");goog.require("goog.debug.HtmlFormatter");goog.require("goog.debug.LogRecord");goog.require("goog.debug.Logger");goog.require("goog.html.SafeHtml");goog.require("goog.testing.jsunit");var EXPECTED_RECORD_HTML_RE="^prefix \\[.*?\\] \\[ &#160;.*?s\\] \\[loggerName\\] "+"<span class=\"dbg-f\">mess<br> &#160;age<br>Message:",logRecord;function setUp(){logRecord=new goog.debug.LogRecord(goog.debug.Logger.Level.CONFIG,"mess\n  age","loggerName",1,100);logRecord.setException(new Error("exc\n  eption"))}function testHtmlFormatter_formatRecord(){var formatter=new goog.debug.HtmlFormatter("prefix"),actual=formatter.formatRecord(logRecord);assertRegExp(EXPECTED_RECORD_HTML_RE,actual)}function testHtmlFormatter_formatRecordAsHtml(){var formatter=new goog.debug.HtmlFormatter("prefix"),actual=formatter.formatRecordAsHtml(logRecord);assertRegExp(EXPECTED_RECORD_HTML_RE,goog.html.SafeHtml.unwrap(actual))}