goog.provide("goog.demos.SampleComponent");goog.require("goog.dom");goog.require("goog.dom.TagName");goog.require("goog.dom.classlist");goog.require("goog.events.EventType");goog.require("goog.events.KeyCodes");goog.require("goog.events.KeyHandler");goog.require("goog.ui.Component");goog.demos.SampleComponent=function(opt_label,opt_domHelper){goog.demos.SampleComponent.base(this,"constructor",opt_domHelper);this.initialLabel_=opt_label||"Click Me";this.color_="red";this.kh_=null};goog.inherits(goog.demos.SampleComponent,goog.ui.Component);goog.demos.SampleComponent.prototype.changeColor_=function(){if("red"==this.color_){this.color_="green"}else if("green"==this.color_){this.color_="blue"}else{this.color_="red"}this.getElement().style.backgroundColor=this.color_};goog.demos.SampleComponent.prototype.createDom=function(){this.decorateInternal(this.dom_.createElement(goog.dom.TagName.DIV))};goog.demos.SampleComponent.prototype.decorateInternal=function(element){goog.demos.SampleComponent.base(this,"decorateInternal",element);if(!this.getLabelText()){this.setLabelText(this.initialLabel_)}var elem=this.getElement();goog.dom.classlist.add(elem,goog.getCssName("goog-sample-component"));elem.style.backgroundColor=this.color_;elem.tabIndex=0;this.kh_=new goog.events.KeyHandler(elem);this.getHandler().listen(this.kh_,goog.events.KeyHandler.EventType.KEY,this.onKey_)};goog.demos.SampleComponent.prototype.disposeInternal=function(){goog.demos.SampleComponent.base(this,"disposeInternal");if(this.kh_){this.kh_.dispose()}};goog.demos.SampleComponent.prototype.enterDocument=function(){goog.demos.SampleComponent.base(this,"enterDocument");this.getHandler().listen(this.getElement(),goog.events.EventType.CLICK,this.onDivClicked_)};goog.demos.SampleComponent.prototype.getLabelText=function(){if(!this.getElement()){return""}return goog.dom.getTextContent(this.getElement())};goog.demos.SampleComponent.prototype.onDivClicked_=function(event){this.changeColor_()};goog.demos.SampleComponent.prototype.onKey_=function(event){var keyCodes=goog.events.KeyCodes;if(event.keyCode==keyCodes.SPACE||event.keyCode==keyCodes.ENTER){this.changeColor_()}};goog.demos.SampleComponent.prototype.setLabelText=function(text){if(this.getElement()){goog.dom.setTextContent(this.getElement(),text)}};