goog.provide("goog.dom.browserrange.GeckoRange");goog.require("goog.dom.browserrange.W3cRange");goog.dom.browserrange.GeckoRange=function(range){goog.dom.browserrange.W3cRange.call(this,range)};goog.inherits(goog.dom.browserrange.GeckoRange,goog.dom.browserrange.W3cRange);goog.dom.browserrange.GeckoRange.createFromNodeContents=function(node){return new goog.dom.browserrange.GeckoRange(goog.dom.browserrange.W3cRange.getBrowserRangeForNode(node))};goog.dom.browserrange.GeckoRange.createFromNodes=function(startNode,startOffset,endNode,endOffset){return new goog.dom.browserrange.GeckoRange(goog.dom.browserrange.W3cRange.getBrowserRangeForNodes(startNode,startOffset,endNode,endOffset))};goog.dom.browserrange.GeckoRange.prototype.selectInternal=function(selection,reversed){if(!reversed||this.isCollapsed()){goog.dom.browserrange.GeckoRange.base(this,"selectInternal",selection,reversed)}else{selection.collapse(this.getEndNode(),this.getEndOffset());selection.extend(this.getStartNode(),this.getStartOffset())}};