goog.provide("goog.dom.classes");goog.require("goog.array");goog.dom.classes.set=function(element,className){element.className=className};goog.dom.classes.get=function(element){var className=element.className;return goog.isString(className)&&className.match(/\S+/g)||[]};goog.dom.classes.add=function(element,var_args){var classes=goog.dom.classes.get(element),args=goog.array.slice(arguments,1),expectedCount=classes.length+args.length;goog.dom.classes.add_(classes,args);goog.dom.classes.set(element,classes.join(" "));return classes.length==expectedCount};goog.dom.classes.remove=function(element,var_args){var classes=goog.dom.classes.get(element),args=goog.array.slice(arguments,1),newClasses=goog.dom.classes.getDifference_(classes,args);goog.dom.classes.set(element,newClasses.join(" "));return newClasses.length==classes.length-args.length};goog.dom.classes.add_=function(classes,args){for(var i=0;i<args.length;i++){if(!goog.array.contains(classes,args[i])){classes.push(args[i])}}};goog.dom.classes.getDifference_=function(arr1,arr2){return goog.array.filter(arr1,function(item){return!goog.array.contains(arr2,item)})};goog.dom.classes.swap=function(element,fromClass,toClass){for(var classes=goog.dom.classes.get(element),removed=!1,i=0;i<classes.length;i++){if(classes[i]==fromClass){goog.array.splice(classes,i--,1);removed=!0}}if(removed){classes.push(toClass);goog.dom.classes.set(element,classes.join(" "))}return removed};goog.dom.classes.addRemove=function(element,classesToRemove,classesToAdd){var classes=goog.dom.classes.get(element);if(goog.isString(classesToRemove)){goog.array.remove(classes,classesToRemove)}else if(goog.isArray(classesToRemove)){classes=goog.dom.classes.getDifference_(classes,classesToRemove)}if(goog.isString(classesToAdd)&&!goog.array.contains(classes,classesToAdd)){classes.push(classesToAdd)}else if(goog.isArray(classesToAdd)){goog.dom.classes.add_(classes,classesToAdd)}goog.dom.classes.set(element,classes.join(" "))};goog.dom.classes.has=function(element,className){return goog.array.contains(goog.dom.classes.get(element),className)};goog.dom.classes.enable=function(element,className,enabled){if(enabled){goog.dom.classes.add(element,className)}else{goog.dom.classes.remove(element,className)}};goog.dom.classes.toggle=function(element,className){var add=!goog.dom.classes.has(element,className);goog.dom.classes.enable(element,className,add);return add};