goog.provide("goog.dom.classlist");goog.require("goog.array");goog.define("goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST",!1);goog.dom.classlist.get=function(element){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){return element.classList}var className=element.className;return goog.isString(className)&&className.match(/\S+/g)||[]};goog.dom.classlist.set=function(element,className){element.className=className};goog.dom.classlist.contains=function(element,className){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){return element.classList.contains(className)}return goog.array.contains(goog.dom.classlist.get(element),className)};goog.dom.classlist.add=function(element,className){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){element.classList.add(className);return}if(!goog.dom.classlist.contains(element,className)){element.className+=0<element.className.length?" "+className:className}};goog.dom.classlist.addAll=function(element,classesToAdd){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){goog.array.forEach(classesToAdd,function(className){goog.dom.classlist.add(element,className)});return}var classMap={};goog.array.forEach(goog.dom.classlist.get(element),function(className){classMap[className]=!0});goog.array.forEach(classesToAdd,function(className){classMap[className]=!0});element.className="";for(var className in classMap){element.className+=0<element.className.length?" "+className:className}};goog.dom.classlist.remove=function(element,className){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){element.classList.remove(className);return}if(goog.dom.classlist.contains(element,className)){element.className=goog.array.filter(goog.dom.classlist.get(element),function(c){return c!=className}).join(" ")}};goog.dom.classlist.removeAll=function(element,classesToRemove){if(goog.dom.classlist.ALWAYS_USE_DOM_TOKEN_LIST||element.classList){goog.array.forEach(classesToRemove,function(className){goog.dom.classlist.remove(element,className)});return}element.className=goog.array.filter(goog.dom.classlist.get(element),function(className){return!goog.array.contains(classesToRemove,className)}).join(" ")};goog.dom.classlist.enable=function(element,className,enabled){if(enabled){goog.dom.classlist.add(element,className)}else{goog.dom.classlist.remove(element,className)}};goog.dom.classlist.enableAll=function(element,classesToEnable,enabled){var f=enabled?goog.dom.classlist.addAll:goog.dom.classlist.removeAll;f(element,classesToEnable)};goog.dom.classlist.swap=function(element,fromClass,toClass){if(goog.dom.classlist.contains(element,fromClass)){goog.dom.classlist.remove(element,fromClass);goog.dom.classlist.add(element,toClass);return!0}return!1};goog.dom.classlist.toggle=function(element,className){var add=!goog.dom.classlist.contains(element,className);goog.dom.classlist.enable(element,className,add);return add};goog.dom.classlist.addRemove=function(element,classToRemove,classToAdd){goog.dom.classlist.remove(element,classToRemove);goog.dom.classlist.add(element,classToAdd)};