goog.provide("goog.dom.fullscreen_test");goog.setTestOnly("goog.dom.fullscreen_test");goog.require("goog.dom.DomHelper");goog.require("goog.dom.fullscreen");goog.require("goog.testing.PropertyReplacer");goog.require("goog.testing.asserts");goog.require("goog.testing.jsunit");var domHelper,mock_doc,stubs;function clearFullscreenElement(){mock_doc.webkitFullscreenElement=null;mock_doc.mozFullScreenElement=null;mock_doc.msFullscreenElement=null;mock_doc.fullscreenElement=null}function setUp(){mock_doc={};clearFullscreenElement();domHelper=new goog.dom.DomHelper;stubs=new goog.testing.PropertyReplacer;stubs.replace(domHelper,"getDocument",function(){return mock_doc})}function testGetFullScreenElement(){var element={};mock_doc.webkitFullscreenElement=element;assertEquals(element,goog.dom.fullscreen.getFullScreenElement(domHelper))}function testGetFullScreenElementNotFullScreen(){assertNull(goog.dom.fullscreen.getFullScreenElement(domHelper))}