goog.provide("goog.dom.iframeTest");goog.setTestOnly("goog.dom.iframeTest");goog.require("goog.dom");goog.require("goog.dom.iframe");goog.require("goog.html.SafeHtml");goog.require("goog.html.SafeStyle");goog.require("goog.string.Const");goog.require("goog.testing.jsunit");var domHelper,sandbox;function setUpPage(){domHelper=goog.dom.getDomHelper();sandbox=domHelper.getElement("sandbox")}function setUp(){goog.dom.removeChildren(sandbox)}function testCreateWithContent_safeTypes(){var head=goog.html.SafeHtml.create("title",{},"Foo Title"),body=goog.html.SafeHtml.create("div",{id:"blah"},"Test"),style=goog.html.SafeStyle.fromConstant(goog.string.Const.from("position: absolute;")),iframe=goog.dom.iframe.createWithContent(sandbox,head,body,style,!1),doc=goog.dom.getFrameContentDocument(iframe);assertNotNull(doc.getElementById("blah"));assertEquals("Foo Title",doc.title);assertEquals("absolute",iframe.style.position)}function testCreateBlankYieldsIframeWithNoBorderOrPadding(){var iframe=goog.dom.iframe.createBlank(domHelper);iframe.style.width="350px";iframe.style.height="250px";var blankElement=domHelper.getElement("blank");blankElement.appendChild(iframe);assertEquals("Width should be as styled: no extra borders, padding, etc.",350,blankElement.offsetWidth);assertEquals("Height should be as styled: no extra borders, padding, etc.",250,blankElement.offsetHeight)}function testCreateBlankWithSafeStyles(){var iframe=goog.dom.iframe.createBlank(domHelper,goog.html.SafeStyle.fromConstant(goog.string.Const.from("position:absolute;")));assertEquals("absolute",iframe.style.position);assertEquals("bottom",iframe.style.verticalAlign)}