goog.provide("goog.dom.iterTest");goog.setTestOnly("goog.dom.iterTest");goog.require("goog.dom");goog.require("goog.dom.NodeType");goog.require("goog.dom.iter.AncestorIterator");goog.require("goog.dom.iter.ChildIterator");goog.require("goog.dom.iter.SiblingIterator");goog.require("goog.testing.dom");goog.require("goog.testing.jsunit");var test,br;function setUpPage(){test=goog.dom.getElement("test");br=goog.dom.getElement("br")}function testNextSibling(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.SiblingIterator(test.firstChild),["#br","def"])}function testNextSiblingInclusive(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.SiblingIterator(test.firstChild,!0),["abc","#br","def"])}function testPreviousSibling(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.SiblingIterator(test.lastChild,!1,!0),["#br","abc"])}function testPreviousSiblingInclusive(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.SiblingIterator(test.lastChild,!0,!0),["def","#br","abc"])}function testChildIterator(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.ChildIterator(test),["abc","#br","def"])}function testChildIteratorIndex(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.ChildIterator(test,!1,1),["#br","def"])}function testChildIteratorReverse(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.ChildIterator(test,!0),["def","#br","abc"])}function testEmptyChildIteratorReverse(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.ChildIterator(br,!0),[])}function testChildIteratorIndexReverse(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.ChildIterator(test,!0,1),["#br","abc"])}function testAncestorIterator(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.AncestorIterator(br),["#test","#body","#html",goog.dom.NodeType.DOCUMENT])}function testAncestorIteratorInclusive(){goog.testing.dom.assertNodesMatch(new goog.dom.iter.AncestorIterator(br,!0),["#br","#test","#body","#html",goog.dom.NodeType.DOCUMENT])}