goog.provide("goog.dom.pattern.callback.Counter");goog.dom.pattern.callback.Counter=function(){this.count=0;this.callback_=null};goog.dom.pattern.callback.Counter.prototype.getCallback=function(){if(!this.callback_){this.callback_=goog.bind(function(){this.count++;return!1},this)}return this.callback_};goog.dom.pattern.callback.Counter.prototype.reset=function(){this.count=0};