goog.provide("goog.editor.ContentEditableFieldTest");goog.setTestOnly("goog.editor.ContentEditableFieldTest");goog.require("goog.dom");goog.require("goog.editor.ContentEditableField");goog.require("goog.editor.field_test");goog.require("goog.testing.jsunit");FieldConstructor=goog.editor.ContentEditableField;function testNoIframeAndSameElement(){var field=new goog.editor.ContentEditableField("testField");field.makeEditable();assertFalse(field.usesIframe());assertEquals("Original element should equal field element",field.getOriginalElement(),field.getElement());assertEquals("Sanity check on original element","testField",field.getOriginalElement().id);assertEquals("Editable document should be same as main document",document,field.getEditableDomHelper().getDocument());field.dispose()}function testMakeEditableAndUnEditable(){var elem=goog.dom.getElement("testField");goog.dom.setTextContent(elem,"Hello world");var field=new goog.editor.ContentEditableField("testField");field.makeEditable();assertEquals("true",elem.contentEditable+"");assertEquals("Hello world",goog.dom.getTextContent(elem));field.setHtml(!1,"Goodbye world");assertEquals("Goodbye world",goog.dom.getTextContent(elem));field.makeUneditable();assertNotEquals("true",elem.contentEditable+"");assertEquals("Goodbye world",goog.dom.getTextContent(elem));field.dispose()}