goog.provide("goog.format.JsonPrettyPrinterTest");goog.setTestOnly("goog.format.JsonPrettyPrinterTest");goog.require("goog.format.JsonPrettyPrinter");goog.require("goog.testing.jsunit");var formatter;function setUp(){formatter=new goog.format.JsonPrettyPrinter}function testUndefined(){assertEquals("",formatter.format())}function testNull(){assertEquals("",formatter.format(null))}function testBoolean(){assertEquals("true",formatter.format(!0))}function testNumber(){assertEquals("1",formatter.format(1))}function testEmptyString(){assertEquals("",formatter.format(""))}function testWhitespaceString(){assertEquals("",formatter.format("   "))}function testString(){assertEquals("{}",formatter.format("{}"))}function testEmptyArray(){assertEquals("[]",formatter.format([]))}function testArrayOneElement(){assertEquals("[\n  1\n]",formatter.format([1]))}function testArrayMultipleElements(){assertEquals("[\n  1,\n  2,\n  3\n]",formatter.format([1,2,3]))}function testFunction(){assertEquals("{\n  \"a\": \"1\",\n  \"b\": \"\"\n}",formatter.format({a:"1",b:function(){return null}}))}function testObject(){assertEquals("{}",formatter.format({}))}function testObjectMultipleProperties(){assertEquals("{\n  \"a\": null,\n  \"b\": true,\n  \"c\": 1,\n  \"d\": \"d\",\n  \"e\":"+" [\n    1,\n    2,\n    3\n  ],\n  \"f\": {\n    \"g\": 1,\n    \"h\": \"h\"\n"+"  }\n}",formatter.format({a:null,b:!0,c:1,d:"d",e:[1,2,3],f:{g:1,h:"h"}}))}function testSafeHtmlDelimiters(){var htmlFormatter=new goog.format.JsonPrettyPrinter(new goog.format.JsonPrettyPrinter.SafeHtmlDelimiters());assertEquals("{\n  <span class=\"goog-jsonprettyprinter-propertyname\">&quot;"+"a&lt;b&quot;</span>: <span class=\"goog-jsonprettyprinter-propertyvalue"+"-string\">&quot;&gt;&quot;</span>\n}",htmlFormatter.formatSafeHtml({"a<b":">"}).getTypedStringValue())}