goog.provide("goog.graphics.SolidFill");goog.require("goog.graphics.Fill");goog.graphics.SolidFill=function(color,opt_opacity){this.color_=color;this.opacity_=null==opt_opacity?1:opt_opacity};goog.inherits(goog.graphics.SolidFill,goog.graphics.Fill);goog.graphics.SolidFill.prototype.getColor=function(){return this.color_};goog.graphics.SolidFill.prototype.getOpacity=function(){return this.opacity_};