goog.provide("goog.graphics.SolidFillTest");goog.setTestOnly("goog.graphics.SolidFillTest");goog.require("goog.graphics.SolidFill");goog.require("goog.testing.jsunit");function testGetColor(){var fill=new goog.graphics.SolidFill("#123");assertEquals("#123",fill.getColor());fill=new goog.graphics.SolidFill("#abcdef");assertEquals("#abcdef",fill.getColor());fill=new goog.graphics.SolidFill("#123",.5);assertEquals("#123",fill.getColor());fill=new goog.graphics.SolidFill("#abcdef",.5);assertEquals("#abcdef",fill.getColor())}function testGetOpacity(){var fill=new goog.graphics.SolidFill("#123");assertEquals(1,fill.getOpacity());var fill=new goog.graphics.SolidFill("#123",1);assertEquals(1,fill.getOpacity());fill=new goog.graphics.SolidFill("#123",.5);assertEquals(.5,fill.getOpacity());fill=new goog.graphics.SolidFill("#123",0);assertEquals(0,fill.getOpacity())}