goog.provide("goog.graphics.Stroke");goog.graphics.Stroke=function(width,color,opt_opacity){this.width_=width;this.color_=color;this.opacity_=null==opt_opacity?1:opt_opacity};goog.graphics.Stroke.prototype.getWidth=function(){return this.width_};goog.graphics.Stroke.prototype.getColor=function(){return this.color_};goog.graphics.Stroke.prototype.getOpacity=function(){return this.opacity_};