goog.provide("goog.html.sanitizer.unsafe");goog.require("goog.asserts");goog.require("goog.html.sanitizer.HtmlSanitizer.Builder");goog.require("goog.string");goog.require("goog.string.Const");goog.html.sanitizer.unsafe.alsoAllowTags=function(justification,builder,tags){goog.asserts.assertString(goog.string.Const.unwrap(justification),"must provide justification");goog.asserts.assert(!goog.string.isEmptyOrWhitespace(goog.string.Const.unwrap(justification)),"must provide non-empty justification");return builder.alsoAllowTagsPrivateDoNotAccessOrElse(tags)};goog.html.sanitizer.unsafe.alsoAllowAttributes=function(justification,builder,attrs){goog.asserts.assertString(goog.string.Const.unwrap(justification),"must provide justification");goog.asserts.assert(!goog.string.isEmptyOrWhitespace(goog.string.Const.unwrap(justification)),"must provide non-empty justification");return builder.alsoAllowAttributesPrivateDoNotAccessOrElse(attrs)};goog.html.sanitizer.unsafe.keepUnsanitizedTemplateContents=function(justification,builder){goog.asserts.assertString(goog.string.Const.unwrap(justification),"must provide justification");goog.asserts.assert(!goog.string.isEmptyOrWhitespace(goog.string.Const.unwrap(justification)),"must provide non-empty justification");return builder.keepUnsanitizedTemplateContentsPrivateDoNotAccessOrElse()};