goog.provide("goog.html.utils");goog.require("goog.string");goog.html.utils.stripHtmlTags=function(value){return goog.string.unescapeEntities(goog.string.trim(value.replace(goog.html.utils.HTML_TAG_REGEX_,function(fullMatch,tagName){return goog.html.utils.INLINE_HTML_TAG_REGEX_.test(tagName)?"":" "}).replace(/[\t\n ]+/g," ")))};goog.html.utils.INLINE_HTML_TAG_REGEX_=/^(?:abbr|acronym|address|b|em|i|small|strong|su[bp]|u)$/i;goog.html.utils.HTML_TAG_REGEX_=/<[!\/]?([a-z0-9]+)([\/ ][^>]*)?>/gi;