goog.provide("goog.i18n.collation");goog.i18n.collation.createComparator=function(opt_locale,opt_options){if(goog.i18n.collation.hasNativeComparator()){var intl=goog.global.Intl;return new intl.Collator([opt_locale||goog.LOCALE],opt_options||{}).compare}else{return function(arg1,arg2){return arg1.localeCompare(arg2)}}};goog.i18n.collation.hasNativeComparator=function(opt_locale){var intl=goog.global.Intl;return!!(intl&&intl.Collator)};