goog.provide("goog.i18n.collationTest");goog.setTestOnly("goog.i18n.collationTest");goog.require("goog.i18n.collation");goog.require("goog.testing.ExpectedFailures");goog.require("goog.testing.jsunit");goog.require("goog.userAgent");var expectedFailures;function setUpPage(){expectedFailures=new goog.testing.ExpectedFailures}function tearDown(){expectedFailures.handleTearDown()}function testGetEnComparator(){goog.LOCALE="en";var compare=goog.i18n.collation.createComparator();expectedFailures.expectFailureFor(goog.userAgent.GECKO&&goog.userAgent.LINUX);try{assertTrue(0<compare("c\xF4te","cot\xE9"))}catch(e){expectedFailures.handleException(e)}}function testGetFrComparator(){goog.LOCALE="fr-CA";var compare=goog.i18n.collation.createComparator();if(!goog.i18n.collation.hasNativeComparator())return;assertTrue(0>compare("c\xF4te","cot\xE9"))}function testGetComparatorForSpecificLocale(){goog.LOCALE="en";var compare=goog.i18n.collation.createComparator("fr-CA");if(!goog.i18n.collation.hasNativeComparator("fr-CA"))return;assertTrue(0>compare("c\xF4te","cot\xE9"))}function testGetNumericComparator(){var compare=goog.i18n.collation.createComparator("en",{numeric:!0});if(!goog.i18n.collation.hasNativeComparator("en"))return;assertTrue(0>compare("2","10"))}function testGetNonNumericComparator(){var compare=goog.i18n.collation.createComparator("en",{numeric:!1});if(!goog.i18n.collation.hasNativeComparator("en"))return;assertTrue(0<compare("2","10"))}