goog.provide("goog.i18n.currencyTest");goog.setTestOnly("goog.i18n.currencyTest");goog.require("goog.i18n.NumberFormat");goog.require("goog.i18n.currency");goog.require("goog.i18n.currency.CurrencyInfo");goog.require("goog.object");goog.require("goog.testing.PropertyReplacer");goog.require("goog.testing.jsunit");var stubs=new goog.testing.PropertyReplacer;function setUp(){stubs.replace(goog.i18n.currency,"CurrencyInfo",goog.object.clone(goog.i18n.currency.CurrencyInfo))}function tearDown(){stubs.reset()}function testAddTier2Support(){assertFalse("LRD"in goog.i18n.currency.CurrencyInfo);assertThrows(function(){goog.i18n.currency.getLocalCurrencyPattern("LRD")});goog.i18n.currency.addTier2Support();assertTrue("LRD"in goog.i18n.currency.CurrencyInfo);assertEquals("'$'#,##0.00",goog.i18n.currency.getLocalCurrencyPattern("LRD"))}function testCurrencyPattern(){assertEquals("'$'#,##0.00",goog.i18n.currency.getLocalCurrencyPattern("USD"));assertEquals("'US$'#,##0.00",goog.i18n.currency.getPortableCurrencyPattern("USD"));assertEquals("USD '$'#,##0.00",goog.i18n.currency.getGlobalCurrencyPattern("USD"));assertEquals("'\xA5'#,##0",goog.i18n.currency.getLocalCurrencyPattern("JPY"));assertEquals("'JP\xA5'#,##0",goog.i18n.currency.getPortableCurrencyPattern("JPY"));assertEquals("JPY '\xA5'#,##0",goog.i18n.currency.getGlobalCurrencyPattern("JPY"));assertEquals("'\u20AC'#,##0.00",goog.i18n.currency.getLocalCurrencyPattern("EUR"));assertEquals("'\u20AC'#,##0.00",goog.i18n.currency.getPortableCurrencyPattern("EUR"));assertEquals("EUR '\u20AC'#,##0.00",goog.i18n.currency.getGlobalCurrencyPattern("EUR"));assertEquals("'\xA5'#,##0.00",goog.i18n.currency.getLocalCurrencyPattern("CNY"));assertEquals("'RMB\xA5'#,##0.00",goog.i18n.currency.getPortableCurrencyPattern("CNY"));assertEquals("CNY '\xA5'#,##0.00",goog.i18n.currency.getGlobalCurrencyPattern("CNY"));assertEquals("'Rial'#,##0",goog.i18n.currency.getLocalCurrencyPattern("YER"));assertEquals("'Rial'#,##0",goog.i18n.currency.getPortableCurrencyPattern("YER"));assertEquals("YER 'Rial'#,##0",goog.i18n.currency.getGlobalCurrencyPattern("YER"));assertEquals("'CHF'#,##0.00",goog.i18n.currency.getLocalCurrencyPattern("CHF"));assertEquals("'CHF'#,##0.00",goog.i18n.currency.getPortableCurrencyPattern("CHF"));assertEquals("'CHF'#,##0.00",goog.i18n.currency.getGlobalCurrencyPattern("CHF"))}function testCurrencyFormatCHF(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("CHF"));str=formatter.format(123456.7899);assertEquals("CHF123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("CHF"));str=formatter.format(123456.7899);assertEquals("CHF123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("CHF"));str=formatter.format(123456.7899);assertEquals("CHF123,456.79",str)}function testCurrencyFormatYER(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("YER"));str=formatter.format(123456.7899);assertEquals("Rial123,457",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("YER"));str=formatter.format(123456.7899);assertEquals("Rial123,457",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("YER"));str=formatter.format(123456.7899);assertEquals("YER Rial123,457",str)}function testCurrencyFormatCNY(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("CNY"));str=formatter.format(123456.7899);assertEquals("\xA5123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("CNY"));str=formatter.format(123456.7899);assertEquals("RMB\xA5123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("CNY"));str=formatter.format(123456.7899);assertEquals("CNY \xA5123,456.79",str)}function testCurrencyFormatCZK(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("CZK"));str=formatter.format(123456.7899);assertEquals("123,456.79 K\u010D",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("CZK"));str=formatter.format(123456.7899);assertEquals("123,456.79 K\u010D",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("CZK"));str=formatter.format(123456.7899);assertEquals("CZK 123,456.79 K\u010D",str)}function testCurrencyFormatEUR(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("EUR"));str=formatter.format(123456.7899);assertEquals("\u20AC123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("EUR"));str=formatter.format(123456.7899);assertEquals("\u20AC123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("EUR"));str=formatter.format(123456.7899);assertEquals("EUR \u20AC123,456.79",str)}function testCurrencyFormatJPY(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("JPY"));str=formatter.format(123456.7899);assertEquals("\xA5123,457",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("JPY"));str=formatter.format(123456.7899);assertEquals("JP\xA5123,457",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("JPY"));str=formatter.format(123456.7899);assertEquals("JPY \xA5123,457",str)}function testCurrencyFormatPLN(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("PLN"));str=formatter.format(123456.7899);assertEquals("123,456.79 z\u0142",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("PLN"));str=formatter.format(123456.7899);assertEquals("123,456.79 z\u0142",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("PLN"));str=formatter.format(123456.7899);assertEquals("PLN 123,456.79 z\u0142",str)}function testCurrencyFormatUSD(){var formatter,str;formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getLocalCurrencyPattern("USD"));str=formatter.format(123456.7899);assertEquals("$123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getPortableCurrencyPattern("USD"));str=formatter.format(123456.7899);assertEquals("US$123,456.79",str);formatter=new goog.i18n.NumberFormat(goog.i18n.currency.getGlobalCurrencyPattern("USD"));str=formatter.format(123456.7899);assertEquals("USD $123,456.79",str)}function testIsPrefixSignPosition(){assertTrue(goog.i18n.currency.isPrefixSignPosition("USD"));assertTrue(goog.i18n.currency.isPrefixSignPosition("EUR"))}function testGetCurrencySign(){assertEquals("USD $",goog.i18n.currency.getGlobalCurrencySign("USD"));assertEquals("$",goog.i18n.currency.getLocalCurrencySign("USD"));assertEquals("US$",goog.i18n.currency.getPortableCurrencySign("USD"));assertEquals("YER Rial",goog.i18n.currency.getGlobalCurrencySign("YER"));assertEquals("Rial",goog.i18n.currency.getLocalCurrencySign("YER"));assertEquals("Rial",goog.i18n.currency.getPortableCurrencySign("YER"))}