goog.provide("goog.i18n.uChar.LocalNameFetcherTest");goog.setTestOnly("goog.i18n.uChar.LocalNameFetcherTest");goog.require("goog.i18n.uChar.LocalNameFetcher");goog.require("goog.testing.jsunit");goog.require("goog.testing.recordFunction");var nameFetcher=null;function setUp(){nameFetcher=new goog.i18n.uChar.LocalNameFetcher}function testGetName_exists(){var callback=goog.testing.recordFunction(function(name){assertEquals("Space",name)});nameFetcher.getName(" ",callback);assertEquals(1,callback.getCallCount())}function testGetName_variationSelector(){var callback=goog.testing.recordFunction(function(name){assertEquals("Variation Selector - 1",name)});nameFetcher.getName("\uFE00",callback);assertEquals(1,callback.getCallCount())}function testGetName_missing(){var callback=goog.testing.recordFunction(function(name){assertNull(name)});nameFetcher.getName("P",callback);assertEquals(1,callback.getCallCount())}function testIsNameAvailable_withAvailableName(){assertTrue(nameFetcher.isNameAvailable(" "))}function testIsNameAvailable_withoutAvailableName(){assertFalse(nameFetcher.isNameAvailable("a"))}