goog.provide("goog.i18n.uCharTest");goog.setTestOnly("goog.i18n.uCharTest");goog.require("goog.i18n.uChar");goog.require("goog.testing.jsunit");function testToHexString(){var result=goog.i18n.uChar.toHexString("\uD869\uDED6");assertEquals("U+2A6D6",result)}function testPadString(){var result=goog.i18n.uChar.padString_("abc",4,"0");assertEquals("0abc",result)}function testToCharCode(){var result=goog.i18n.uChar.toCharCode("\uD869\uDED6");assertEquals(173782,result)}function testcodePointAt(){assertEquals(108,goog.i18n.uChar.getCodePointAround("Hello!",2));assertEquals(9992,goog.i18n.uChar.getCodePointAround("Hello\u2708",5));assertEquals(173782,goog.i18n.uChar.getCodePointAround("\uD869\uDED6",0));assertEquals(-173782,goog.i18n.uChar.getCodePointAround("\uD869\uDED6",1));assertEquals(55401,goog.i18n.uChar.getCodePointAround("\uD869"+"w",0));assertEquals(57046,goog.i18n.uChar.getCodePointAround("\uD869"+"w"+"\uDED6",2))}function testBuildSupplementaryCodePoint(){var result=goog.i18n.uChar.buildSupplementaryCodePoint(55401,57046);assertEquals(173782,result);assertNull(goog.i18n.uChar.buildSupplementaryCodePoint(57046,55401));assertNull(goog.i18n.uChar.buildSupplementaryCodePoint(55401,44032))}function testCharCount(){assertEquals(2,goog.i18n.uChar.charCount(173782));assertEquals(1,goog.i18n.uChar.charCount(44032))}function testIsSupplementaryCodePoint(){assertTrue(goog.i18n.uChar.isSupplementaryCodePoint(173782));assertFalse(goog.i18n.uChar.isSupplementaryCodePoint(44032))}function testIsLeadSurrogateCodepoint(){assertTrue(goog.i18n.uChar.isLeadSurrogateCodePoint(55401));assertFalse(goog.i18n.uChar.isLeadSurrogateCodePoint(57046));assertFalse(goog.i18n.uChar.isLeadSurrogateCodePoint(44032))}function testIsTrailSurrogateCodePoint(){assertTrue(goog.i18n.uChar.isTrailSurrogateCodePoint(57046));assertFalse(goog.i18n.uChar.isTrailSurrogateCodePoint(55401));assertFalse(goog.i18n.uChar.isTrailSurrogateCodePoint(44032))}function testFromCharCode(){var result=goog.i18n.uChar.fromCharCode(173782);assertEquals("\uD869\uDED6",result)}function testFromCharCode_invalidValues(){var result=goog.i18n.uChar.fromCharCode(-1);assertEquals(null,result);result=goog.i18n.uChar.fromCharCode(goog.i18n.uChar.CODE_POINT_MAX_VALUE_+1);assertEquals(null,result);result=goog.i18n.uChar.fromCharCode(null);assertEquals(null,result);result=goog.i18n.uChar.fromCharCode(void 0);assertEquals(null,result);result=goog.i18n.uChar.fromCharCode(NaN);assertEquals(null,result)}