goog.provide("goog.labs.events.touch");goog.provide("goog.labs.events.touch.TouchData");goog.require("goog.array");goog.require("goog.asserts");goog.require("goog.events.EventType");goog.require("goog.string");goog.labs.events.touch.TouchData;goog.labs.events.touch.getTouchData=function(e){var source=e;goog.asserts.assert(goog.string.startsWith(e.type,"touch")||goog.string.startsWith(e.type,"mouse"),"Event must be mouse or touch event.");if(goog.string.startsWith(e.type,"touch")){goog.asserts.assert(goog.array.contains([goog.events.EventType.TOUCHCANCEL,goog.events.EventType.TOUCHEND,goog.events.EventType.TOUCHMOVE,goog.events.EventType.TOUCHSTART],e.type),"Touch event not of valid type.");source=e.type==goog.events.EventType.TOUCHEND||e.type==goog.events.EventType.TOUCHCANCEL?e.changedTouches[0]:e.targetTouches[0]}return{clientX:source.clientX,clientY:source.clientY,screenX:source.screenX,screenY:source.screenY,target:source.target}};