goog.provide("goog.labs.events.touchTest");goog.require("goog.labs.events.touch");goog.require("goog.testing.jsunit");goog.setTestOnly("goog.labs.events.touchTest");function testMouseEvent(){var fakeTarget={},fakeMouseMove={clientX:1,clientY:2,screenX:3,screenY:4,target:fakeTarget,type:"mousemove"},data=goog.labs.events.touch.getTouchData(fakeMouseMove);assertEquals(1,data.clientX);assertEquals(2,data.clientY);assertEquals(3,data.screenX);assertEquals(4,data.screenY);assertEquals(fakeTarget,data.target)}function testTouchEvent(){var fakeTarget={},fakeTouch={clientX:1,clientY:2,screenX:3,screenY:4,target:fakeTarget},fakeTouchStart={targetTouches:[fakeTouch],target:fakeTarget,type:"touchstart"},data=goog.labs.events.touch.getTouchData(fakeTouchStart);assertEquals(1,data.clientX);assertEquals(2,data.clientY);assertEquals(3,data.screenX);assertEquals(4,data.screenY);assertEquals(fakeTarget,data.target)}function testTouchChangeEvent(){var fakeTarget={},fakeTouch={clientX:1,clientY:2,screenX:3,screenY:4,target:fakeTarget},fakeTouchStart={changedTouches:[fakeTouch],target:fakeTarget,type:"touchend"},data=goog.labs.events.touch.getTouchData(fakeTouchStart);assertEquals(1,data.clientX);assertEquals(2,data.clientY);assertEquals(3,data.screenX);assertEquals(4,data.screenY);assertEquals(fakeTarget,data.target)}