goog.provide("goog.labs.i18n.GenderInfo");goog.provide("goog.labs.i18n.GenderInfo.Gender");goog.provide("goog.labs.i18n.ListFormat");goog.require("goog.asserts");goog.require("goog.labs.i18n.ListFormatSymbols");goog.labs.i18n.ListFormat=function(){this.listTwoPattern_=goog.labs.i18n.ListFormatSymbols.LIST_TWO;this.listStartPattern_=goog.labs.i18n.ListFormatSymbols.LIST_START;this.listMiddlePattern_=goog.labs.i18n.ListFormatSymbols.LIST_MIDDLE;this.listEndPattern_=goog.labs.i18n.ListFormatSymbols.LIST_END};goog.labs.i18n.ListFormat.prototype.patternBasedJoinTwoStrings_=function(pattern,first,second){return pattern.replace("{0}",first).replace("{1}",second)};goog.labs.i18n.ListFormat.prototype.format=function(items){var count=items.length;switch(count){case 0:return"";case 1:return items[0]+"";case 2:return this.patternBasedJoinTwoStrings_(this.listTwoPattern_,items[0]+"",items[1]+"");}for(var result=this.patternBasedJoinTwoStrings_(this.listStartPattern_,items[0]+"",items[1]+""),i=2;i<count-1;++i){result=this.patternBasedJoinTwoStrings_(this.listMiddlePattern_,result,items[i]+"")}return this.patternBasedJoinTwoStrings_(this.listEndPattern_,result,items[count-1]+"")};goog.labs.i18n.GenderInfo=function(){this.listGenderStyle_=goog.labs.i18n.ListFormatSymbols.GENDER_STYLE};goog.labs.i18n.GenderInfo.ListGenderStyle_={NEUTRAL:0,MIXED_NEUTRAL:1,MALE_TAINTS:2};goog.labs.i18n.GenderInfo.Gender={MALE:0,FEMALE:1,OTHER:2};goog.labs.i18n.GenderInfo.prototype.getListGender=function(genders){var Gender=goog.labs.i18n.GenderInfo.Gender,count=genders.length;if(0==count){return Gender.OTHER}if(1==count){return genders[0]}switch(this.listGenderStyle_){case goog.labs.i18n.GenderInfo.ListGenderStyle_.NEUTRAL:return Gender.OTHER;case goog.labs.i18n.GenderInfo.ListGenderStyle_.MIXED_NEUTRAL:for(var hasFemale=!1,hasMale=!1,i=0;i<count;++i){switch(genders[i]){case Gender.FEMALE:if(hasMale){return Gender.OTHER}hasFemale=!0;break;case Gender.MALE:if(hasFemale){return Gender.OTHER}hasMale=!0;break;case Gender.OTHER:return Gender.OTHER;default:goog.asserts.assert(!1,"Invalid genders["+i+"] = "+genders[i]);return Gender.OTHER;}}return hasMale?Gender.MALE:Gender.FEMALE;case goog.labs.i18n.GenderInfo.ListGenderStyle_.MALE_TAINTS:for(var i=0;i<count;++i){if(genders[i]!=Gender.FEMALE){return Gender.MALE}}return Gender.FEMALE;default:return Gender.OTHER;}};