goog.provide("goog.labs.testing.MatcherError");goog.provide("goog.labs.testing.assertThat");goog.require("goog.debug.Error");goog.labs.testing.assertThat=function(actual,matcher,opt_reason){if(!matcher.matches(actual)){var prefix=opt_reason?opt_reason+": ":"",desc=prefix+matcher.describe(actual);throw new goog.labs.testing.MatcherError(desc)}};goog.labs.testing.MatcherError=function(opt_message){goog.labs.testing.MatcherError.base(this,"constructor",opt_message)};goog.inherits(goog.labs.testing.MatcherError,goog.debug.Error);