goog.provide("goog.labs.testing.decoratorMatcherTest");goog.setTestOnly("goog.labs.testing.decoratorMatcherTest");goog.require("goog.labs.testing.AnythingMatcher");goog.require("goog.labs.testing.GreaterThanMatcher");goog.require("goog.labs.testing.MatcherError");goog.require("goog.labs.testing.assertThat");goog.require("goog.testing.jsunit");function testAnythingMatcher(){goog.labs.testing.assertThat(!0,anything(),"anything matches true");goog.labs.testing.assertThat(!1,anything(),"false matches anything")}function testIs(){goog.labs.testing.assertThat(5,is(greaterThan(4)),"5 is > 4")}function testDescribedAs(){var e=assertThrows(function(){goog.labs.testing.assertThat(4,describedAs("this is a test",greaterThan(6)))});assertTrue(e instanceof goog.labs.testing.MatcherError);assertEquals("this is a test",e.message)}