goog.provide("goog.labs.testing.HasEntriesMatcher");goog.provide("goog.labs.testing.HasEntryMatcher");goog.provide("goog.labs.testing.HasKeyMatcher");goog.provide("goog.labs.testing.HasValueMatcher");goog.require("goog.asserts");goog.require("goog.labs.testing.Matcher");goog.require("goog.object");goog.labs.testing.HasEntriesMatcher=function(entries){this.entries_=entries};goog.labs.testing.HasEntriesMatcher.prototype.matches=function(actualObject){goog.asserts.assertObject(actualObject,"Expected an Object");var object=actualObject;return goog.object.every(this.entries_,function(value,key){return goog.object.containsKey(object,key)&&object[key]===value})};goog.labs.testing.HasEntriesMatcher.prototype.describe=function(actualObject){goog.asserts.assertObject(actualObject,"Expected an Object");var object=actualObject,errorString="Input object did not contain the following entries:\n";goog.object.forEach(this.entries_,function(value,key){if(!goog.object.containsKey(object,key)||object[key]!==value){errorString+=key+": "+value+"\n"}});return errorString};goog.labs.testing.HasEntryMatcher=function(key,value){this.key_=key;this.value_=value};goog.labs.testing.HasEntryMatcher.prototype.matches=function(actualObject){goog.asserts.assertObject(actualObject);return goog.object.containsKey(actualObject,this.key_)&&actualObject[this.key_]===this.value_};goog.labs.testing.HasEntryMatcher.prototype.describe=function(actualObject){goog.asserts.assertObject(actualObject);var errorMsg;if(goog.object.containsKey(actualObject,this.key_)){errorMsg="Input object did not contain key: "+this.key_}else{errorMsg="Value for key did not match value: "+this.value_}return errorMsg};goog.labs.testing.HasKeyMatcher=function(key){this.key_=key};goog.labs.testing.HasKeyMatcher.prototype.matches=function(actualObject){goog.asserts.assertObject(actualObject);return goog.object.containsKey(actualObject,this.key_)};goog.labs.testing.HasKeyMatcher.prototype.describe=function(actualObject){goog.asserts.assertObject(actualObject);return"Input object did not contain the key: "+this.key_};goog.labs.testing.HasValueMatcher=function(value){this.value_=value};goog.labs.testing.HasValueMatcher.prototype.matches=function(actualObject){goog.asserts.assertObject(actualObject,"Expected an Object");var object=actualObject;return goog.object.containsValue(object,this.value_)};goog.labs.testing.HasValueMatcher.prototype.describe=function(actualObject){return"Input object did not contain the value: "+this.value_};function hasEntries(entries){return new goog.labs.testing.HasEntriesMatcher(entries)}function hasEntry(key,value){return new goog.labs.testing.HasEntryMatcher(key,value)}function hasKey(key){return new goog.labs.testing.HasKeyMatcher(key)}function hasValue(value){return new goog.labs.testing.HasValueMatcher(value)}