goog.provide("goog.labs.testing.AllOfMatcher");goog.provide("goog.labs.testing.AnyOfMatcher");goog.provide("goog.labs.testing.IsNotMatcher");goog.require("goog.array");goog.require("goog.labs.testing.Matcher");goog.labs.testing.AllOfMatcher=function(matchers){this.matchers_=matchers};goog.labs.testing.AllOfMatcher.prototype.matches=function(actualValue){return goog.array.every(this.matchers_,function(matcher){return matcher.matches(actualValue)})};goog.labs.testing.AllOfMatcher.prototype.describe=function(actualValue){var errorString="";goog.array.forEach(this.matchers_,function(matcher){if(!matcher.matches(actualValue)){errorString+=matcher.describe(actualValue)+"\n"}});return errorString};goog.labs.testing.AnyOfMatcher=function(matchers){this.matchers_=matchers};goog.labs.testing.AnyOfMatcher.prototype.matches=function(actualValue){return goog.array.some(this.matchers_,function(matcher){return matcher.matches(actualValue)})};goog.labs.testing.AnyOfMatcher.prototype.describe=function(actualValue){var errorString="";goog.array.forEach(this.matchers_,function(matcher){if(!matcher.matches(actualValue)){errorString+=matcher.describe(actualValue)+"\n"}});return errorString};goog.labs.testing.IsNotMatcher=function(matcher){this.matcher_=matcher};goog.labs.testing.IsNotMatcher.prototype.matches=function(actualValue){return!this.matcher_.matches(actualValue)};goog.labs.testing.IsNotMatcher.prototype.describe=function(actualValue){return"The following is false: "+this.matcher_.describe(actualValue)};function allOf(var_args){var matchers=goog.array.toArray(arguments);return new goog.labs.testing.AllOfMatcher(matchers)}function anyOf(var_args){var matchers=goog.array.toArray(arguments);return new goog.labs.testing.AnyOfMatcher(matchers)}function isNot(matcher){return new goog.labs.testing.IsNotMatcher(matcher)}