goog.provide("goog.labs.testing.CloseToMatcher");goog.provide("goog.labs.testing.EqualToMatcher");goog.provide("goog.labs.testing.GreaterThanEqualToMatcher");goog.provide("goog.labs.testing.GreaterThanMatcher");goog.provide("goog.labs.testing.LessThanEqualToMatcher");goog.provide("goog.labs.testing.LessThanMatcher");goog.require("goog.asserts");goog.require("goog.labs.testing.Matcher");goog.labs.testing.GreaterThanMatcher=function(value){this.value_=value};goog.labs.testing.GreaterThanMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue>this.value_};goog.labs.testing.GreaterThanMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not greater than "+this.value_};goog.labs.testing.LessThanMatcher=function(value){this.value_=value};goog.labs.testing.LessThanMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue<this.value_};goog.labs.testing.LessThanMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not less than "+this.value_};goog.labs.testing.GreaterThanEqualToMatcher=function(value){this.value_=value};goog.labs.testing.GreaterThanEqualToMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue>=this.value_};goog.labs.testing.GreaterThanEqualToMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not greater than equal to "+this.value_};goog.labs.testing.LessThanEqualToMatcher=function(value){this.value_=value};goog.labs.testing.LessThanEqualToMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue<=this.value_};goog.labs.testing.LessThanEqualToMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not less than equal to "+this.value_};goog.labs.testing.EqualToMatcher=function(value){this.value_=value};goog.labs.testing.EqualToMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue===this.value_};goog.labs.testing.EqualToMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not equal to "+this.value_};goog.labs.testing.CloseToMatcher=function(value,range){this.value_=value;this.range_=range};goog.labs.testing.CloseToMatcher.prototype.matches=function(actualValue){goog.asserts.assertNumber(actualValue);return Math.abs(this.value_-actualValue)<this.range_};goog.labs.testing.CloseToMatcher.prototype.describe=function(actualValue){goog.asserts.assertNumber(actualValue);return actualValue+" is not close to("+this.range_+") "+this.value_};function greaterThan(value){return new goog.labs.testing.GreaterThanMatcher(value)}function greaterThanEqualTo(value){return new goog.labs.testing.GreaterThanEqualToMatcher(value)}function lessThan(value){return new goog.labs.testing.LessThanMatcher(value)}function lessThanEqualTo(value){return new goog.labs.testing.LessThanEqualToMatcher(value)}function equalTo(value){return new goog.labs.testing.EqualToMatcher(value)}function closeTo(value,range){return new goog.labs.testing.CloseToMatcher(value,range)}