goog.provide("goog.labs.testing.HasPropertyMatcher");goog.provide("goog.labs.testing.InstanceOfMatcher");goog.provide("goog.labs.testing.IsNullMatcher");goog.provide("goog.labs.testing.IsNullOrUndefinedMatcher");goog.provide("goog.labs.testing.IsUndefinedMatcher");goog.provide("goog.labs.testing.ObjectEqualsMatcher");goog.require("goog.labs.testing.Matcher");goog.labs.testing.ObjectEqualsMatcher=function(expectedObject){this.object_=expectedObject};goog.labs.testing.ObjectEqualsMatcher.prototype.matches=function(actualObject){return actualObject===this.object_};goog.labs.testing.ObjectEqualsMatcher.prototype.describe=function(actualObject){return"Input object is not the same as the expected object."};goog.labs.testing.HasPropertyMatcher=function(property){this.property_=property};goog.labs.testing.HasPropertyMatcher.prototype.matches=function(actualObject){return this.property_ in actualObject};goog.labs.testing.HasPropertyMatcher.prototype.describe=function(actualObject){return"Object does not have property: "+this.property_};goog.labs.testing.InstanceOfMatcher=function(object){this.object_=object};goog.labs.testing.InstanceOfMatcher.prototype.matches=function(actualObject){return actualObject instanceof this.object_};goog.labs.testing.InstanceOfMatcher.prototype.describe=function(actualObject){return"Input object is not an instance of the expected object"};goog.labs.testing.IsNullOrUndefinedMatcher=function(){};goog.labs.testing.IsNullOrUndefinedMatcher.prototype.matches=function(actualValue){return!goog.isDefAndNotNull(actualValue)};goog.labs.testing.IsNullOrUndefinedMatcher.prototype.describe=function(actualValue){return actualValue+" is not null or undefined."};goog.labs.testing.IsNullMatcher=function(){};goog.labs.testing.IsNullMatcher.prototype.matches=function(actualValue){return goog.isNull(actualValue)};goog.labs.testing.IsNullMatcher.prototype.describe=function(actualValue){return actualValue+" is not null."};goog.labs.testing.IsUndefinedMatcher=function(){};goog.labs.testing.IsUndefinedMatcher.prototype.matches=function(actualValue){return!goog.isDef(actualValue)};goog.labs.testing.IsUndefinedMatcher.prototype.describe=function(actualValue){return actualValue+" is not undefined."};function equalsObject(object){return new goog.labs.testing.ObjectEqualsMatcher(object)}function hasProperty(property){return new goog.labs.testing.HasPropertyMatcher(property)}function instanceOfClass(object){return new goog.labs.testing.InstanceOfMatcher(object)}function isNull(){return new goog.labs.testing.IsNullMatcher}function isNullOrUndefined(){return new goog.labs.testing.IsNullOrUndefinedMatcher}function isUndefined(){return new goog.labs.testing.IsUndefinedMatcher}