goog.provide("goog.math.interpolator.Pchip1Test");goog.setTestOnly("goog.math.interpolator.Pchip1Test");goog.require("goog.math.interpolator.Pchip1");goog.require("goog.testing.jsunit");function testSpline(){for(var x=[0,1,3,6,7],y=[0,0,0,0,0],i=0;i<x.length;++i){y[i]=Math.sin(x[i])}var interp=new goog.math.interpolator.Pchip1;interp.setData(x,y);for(var xi=[0,.5,1,2,3,4,5,6,7],expected=[0,.5756,.8415,.5428,.1411,-.0595,-.2162,-.2794,.657],result=[0,0,0,0,0,0,0,0,0],i=0;i<xi.length;++i){result[i]=interp.interpolate(xi[i])}assertElementsRoughlyEqual(expected,result,1e-4)}function testOutOfBounds(){var x=[0,1,2,4],y=[2,5,4,2],interp=new goog.math.interpolator.Pchip1;interp.setData(x,y);assertRoughlyEquals(-3,interp.interpolate(-1),1e-4);assertRoughlyEquals(1,interp.interpolate(5),1e-4)}function testInverse(){var x=[0,1,3,6,7],y=[0,2,7,8,10],interp=new goog.math.interpolator.Pchip1;interp.setData(x,y);for(var invInterp=interp.getInverse(),xi=[0,.5,1,2,3,4,5,6,7],yi=[0,.9548,2,4.8938,7,7.3906,7.5902,8,10],expectedX=[0,.888,1,.2852,3,4.1206,4.7379,6,7],resultX=[0,0,0,0,0,0,0,0,0],resultY=[0,0,0,0,0,0,0,0,0],i=0;i<xi.length;++i){resultY[i]=interp.interpolate(xi[i]);resultX[i]=invInterp.interpolate(yi[i])}assertElementsRoughlyEqual(expectedX,resultX,1e-4);assertElementsRoughlyEqual(yi,resultY,1e-4)}