goog.provide("goog.math.interpolator.Spline1Test");goog.setTestOnly("goog.math.interpolator.Spline1Test");goog.require("goog.math.interpolator.Spline1");goog.require("goog.testing.jsunit");function testSpline(){var x=[],y=[],interp=new goog.math.interpolator.Spline1;interp.setData(x,y);assertTrue(isNaN(interp.interpolate(1)));x=[0];y=[2];interp=new goog.math.interpolator.Spline1;interp.setData(x,y);assertRoughlyEquals(2,interp.interpolate(1),1e-4);x=[0,1];y=[2,5];interp=new goog.math.interpolator.Spline1;interp.setData(x,y);assertRoughlyEquals(3.5,interp.interpolate(.5),1e-4);x=[0,1,2];y=[2,5,4];interp=new goog.math.interpolator.Spline1;interp.setData(x,y);assertRoughlyEquals(4,interp.interpolate(.5),1e-4);assertRoughlyEquals(-1,interp.interpolate(3),1e-4);x=[0,1,3,6,7];y=[0,0,0,0,0];for(var i=0;i<x.length;++i){y[i]=Math.sin(x[i])}interp=new goog.math.interpolator.Spline1;interp.setData(x,y);for(var xi=[0,.5,1,2,3,4,5,6,7],expected=[0,.5775,.8415,.7047,.1411,-.3601,-.5594,-.2794,.657],result=[0,0,0,0,0,0,0,0,0],i=0;i<xi.length;++i){result[i]=interp.interpolate(xi[i])}assertElementsRoughlyEqual(expected,result,1e-4)}function testOutOfBounds(){var x=[0,1,2,4],y=[2,5,4,1],interp=new goog.math.interpolator.Spline1;interp.setData(x,y);assertRoughlyEquals(-7.75,interp.interpolate(-1),1e-4);assertRoughlyEquals(4.5,interp.interpolate(5),1e-4)}function testInverse(){var x=[0,1,3,6,7],y=[0,2,7,8,10],interp=new goog.math.interpolator.Spline1;interp.setData(x,y);for(var invInterp=interp.getInverse(),xi=[0,.5,1,2,3,4,5,6,7],yi=[0,.8159,2,4.7892,7,7.6912,7.6275,8,10],expectedX=[0,.8142,1,.2638,3,5.0534,4.8544,6,7],resultX=[0,0,0,0,0,0,0,0,0],resultY=[0,0,0,0,0,0,0,0,0],i=0;i<xi.length;++i){resultY[i]=interp.interpolate(xi[i]);resultX[i]=invInterp.interpolate(yi[i])}assertElementsRoughlyEqual(expectedX,resultX,1e-4);assertElementsRoughlyEqual(yi,resultY,1e-4)}