goog.provide("goog.math.Size");goog.math.Size=function(width,height){this.width=width;this.height=height};goog.math.Size.equals=function(a,b){if(a==b){return!0}if(!a||!b){return!1}return a.width==b.width&&a.height==b.height};goog.math.Size.prototype.clone=function(){return new goog.math.Size(this.width,this.height)};if(goog.DEBUG){goog.math.Size.prototype.toString=function(){return"("+this.width+" x "+this.height+")"}}goog.math.Size.prototype.getLongest=function(){return Math.max(this.width,this.height)};goog.math.Size.prototype.getShortest=function(){return Math.min(this.width,this.height)};goog.math.Size.prototype.area=function(){return this.width*this.height};goog.math.Size.prototype.perimeter=function(){return 2*(this.width+this.height)};goog.math.Size.prototype.aspectRatio=function(){return this.width/this.height};goog.math.Size.prototype.isEmpty=function(){return!this.area()};goog.math.Size.prototype.ceil=function(){var _Mathceil=Math.ceil;this.width=_Mathceil(this.width);this.height=_Mathceil(this.height);return this};goog.math.Size.prototype.fitsInside=function(target){return this.width<=target.width&&this.height<=target.height};goog.math.Size.prototype.floor=function(){var _Mathfloor=Math.floor;this.width=_Mathfloor(this.width);this.height=_Mathfloor(this.height);return this};goog.math.Size.prototype.round=function(){var _Mathround=Math.round;this.width=_Mathround(this.width);this.height=_Mathround(this.height);return this};goog.math.Size.prototype.scale=function(sx,opt_sy){var sy=goog.isNumber(opt_sy)?opt_sy:sx;this.width*=sx;this.height*=sy;return this};goog.math.Size.prototype.scaleToCover=function(target){var s=this.aspectRatio()<=target.aspectRatio()?target.width/this.width:target.height/this.height;return this.scale(s)};goog.math.Size.prototype.scaleToFit=function(target){var s=this.aspectRatio()>target.aspectRatio()?target.width/this.width:target.height/this.height;return this.scale(s)};