goog.provide("goog.messaging.LoggerServer");goog.require("goog.Disposable");goog.require("goog.log");goog.require("goog.log.Level");goog.messaging.LoggerServer=function(channel,serviceName,opt_channelName){goog.messaging.LoggerServer.base(this,"constructor");this.channel_=channel;this.serviceName_=serviceName;this.channelName_=opt_channelName||"remote logger";this.channel_.registerService(this.serviceName_,goog.bind(this.log_,this),!0)};goog.inherits(goog.messaging.LoggerServer,goog.Disposable);goog.messaging.LoggerServer.prototype.log_=function(message){var args=message,level=goog.log.Level.getPredefinedLevelByValue(args.level);if(level){var msg="["+this.channelName_+"] "+args.message;goog.log.getLogger(args.name).log(level,msg,args.exception)}};goog.messaging.LoggerServer.prototype.disposeInternal=function(){goog.messaging.LoggerServer.base(this,"disposeInternal");this.channel_.registerService(this.serviceName_,goog.nullFunction,!0);delete this.channel_};