goog.provide("goog.testing.messaging.MockMessageChannelTest");goog.setTestOnly("goog.testing.messaging.MockMessageChannelTest");goog.require("goog.messaging");goog.require("goog.testing.MockControl");goog.require("goog.testing.jsunit");goog.require("goog.testing.messaging.MockMessageChannel");function testPipe(){var mockControl=new goog.testing.MockControl,ch1=new goog.testing.messaging.MockMessageChannel(mockControl),ch2=new goog.testing.messaging.MockMessageChannel(mockControl);ch1.send("ping","HELLO");ch2.send("pong",{key:"value"});goog.messaging.pipe(ch1,ch2);mockControl.$replayAll();ch2.receive("ping","HELLO");ch1.receive("pong",{key:"value"});mockControl.$verifyAll()}