self.CLOSURE_BASE_PATH="../../";importScripts("../../bootstrap/webworkers.js");importScripts("../../base.js");goog.provide("goog.messaging.testdata.portchannel_worker");goog.require("goog.messaging.PortChannel");function registerPing(channel){channel.registerService("ping",function(msg){channel.send("pong",msg)},!0)}function startListening(){var channel=new goog.messaging.PortChannel(self);registerPing(channel);channel.registerService("addPort",function(port){port.start();registerPing(new goog.messaging.PortChannel(port))},!0)}startListening();postMessage("loaded");