goog.provide("goog.net.xpc.DirectTransportTest");goog.require("goog.Promise");goog.require("goog.dom");goog.require("goog.dom.TagName");goog.require("goog.labs.userAgent.browser");goog.require("goog.log");goog.require("goog.log.Level");goog.require("goog.net.xpc");goog.require("goog.net.xpc.CfgFields");goog.require("goog.net.xpc.CrossPageChannel");goog.require("goog.net.xpc.CrossPageChannelRole");goog.require("goog.net.xpc.TransportTypes");goog.require("goog.testing.TestCase");goog.require("goog.testing.jsunit");goog.setTestOnly("goog.net.xpc.DirectTransportTest");var ECHO_SERVICE_NAME="echo",RESPONSE_SERVICE_NAME="response",MESSAGE_PAYLOAD_1="This is message payload 1.",PEER_IFRAME_ID="peer-iframe",CfgFields=goog.net.xpc.CfgFields,CrossPageChannel=goog.net.xpc.CrossPageChannel,CrossPageChannelRole=goog.net.xpc.CrossPageChannelRole,TransportTypes=goog.net.xpc.TransportTypes,outerXpc,innerXpc,peerIframe,channelName,messageIsSync=!1,savedHtml,debugDiv;function setUpPage(){debugDiv=goog.dom.createElement(goog.dom.TagName.DIV);var logger=goog.log.getLogger("goog.net.xpc");logger.setLevel(goog.log.Level.ALL);goog.log.addHandler(logger,function(logRecord){var msgElm=goog.dom.createDom(goog.dom.TagName.DIV);msgElm.innerHTML=logRecord.getMessage();goog.dom.appendChild(debugDiv,msgElm)});goog.testing.TestCase.getActiveTestCase().promiseTimeout=1e4}function setUp(){savedHtml=document.body.innerHTML;document.body.appendChild(debugDiv)}function tearDown(){if(peerIframe){document.body.removeChild(peerIframe);peerIframe=null}if(outerXpc){outerXpc.dispose();outerXpc=null}if(innerXpc){innerXpc.dispose();innerXpc=null}window.iframeLoadHandler=null;channelName=null;messageIsSync=!1;document.body.innerHTML=savedHtml}function createIframe(){peerIframe=goog.dom.createElement(goog.dom.TagName.IFRAME);peerIframe.id=PEER_IFRAME_ID;document.body.insertBefore(peerIframe,document.body.firstChild)}function testDirectTransport(){if(goog.labs.userAgent.browser.isIE()){return}createIframe();channelName=goog.net.xpc.getRandomString(10);outerXpc=new CrossPageChannel(getConfiguration(CrossPageChannelRole.OUTER,PEER_IFRAME_ID));outerXpc.registerService(ECHO_SERVICE_NAME,goog.nullFunction);var resolver=goog.Promise.withResolver();outerXpc.registerService(RESPONSE_SERVICE_NAME,function(message){assertEquals("Received payload is equal to sent payload.",message,MESSAGE_PAYLOAD_1);resolver.resolve()});outerXpc.connect(function(){assertTrue("XPC over direct channel is connected",outerXpc.isConnected());outerXpc.send(ECHO_SERVICE_NAME,MESSAGE_PAYLOAD_1)});window.iframeLoadHandler=function(){peerIframe.contentWindow.instantiateChannel(getConfiguration(CrossPageChannelRole.INNER))};peerIframe.src="testdata/inner_peer.html";return resolver.promise}function testSameWindowDirectTransport(){channelName=goog.net.xpc.getRandomString(10);outerXpc=new CrossPageChannel(getConfiguration(CrossPageChannelRole.OUTER));outerXpc.setPeerWindowObject(self);outerXpc.registerService(ECHO_SERVICE_NAME,goog.nullFunction);var resolver=goog.Promise.withResolver();outerXpc.registerService(RESPONSE_SERVICE_NAME,function(message){assertEquals("Received payload is equal to sent payload.",message,MESSAGE_PAYLOAD_1);resolver.resolve()});outerXpc.connect(function(){assertTrue("XPC over direct channel, same window, is connected",outerXpc.isConnected());outerXpc.send(ECHO_SERVICE_NAME,MESSAGE_PAYLOAD_1)});innerXpc=new CrossPageChannel(getConfiguration(CrossPageChannelRole.INNER));innerXpc.setPeerWindowObject(self);innerXpc.registerService(ECHO_SERVICE_NAME,function(message){innerXpc.send(RESPONSE_SERVICE_NAME,message)});innerXpc.registerService(RESPONSE_SERVICE_NAME,goog.nullFunction);innerXpc.connect();return resolver.promise}function getConfiguration(role,opt_peerFrameId){var cfg={};cfg[CfgFields.TRANSPORT]=TransportTypes.DIRECT;if(goog.isDefAndNotNull(opt_peerFrameId)){cfg[CfgFields.IFRAME_ID]=opt_peerFrameId}cfg[CfgFields.CHANNEL_NAME]=channelName;cfg[CfgFields.ROLE]=role;return cfg}function testSyncMode(){if(goog.labs.userAgent.browser.isIE()){return}createIframe();channelName=goog.net.xpc.getRandomString(10);var cfg=getConfiguration(CrossPageChannelRole.OUTER,PEER_IFRAME_ID);cfg[CfgFields.DIRECT_TRANSPORT_SYNC_MODE]=!0;outerXpc=new CrossPageChannel(cfg);outerXpc.registerService(ECHO_SERVICE_NAME,goog.nullFunction);var resolver=goog.Promise.withResolver();outerXpc.registerService(RESPONSE_SERVICE_NAME,function(message){assertTrue("The message response was syncronous",messageIsSync);assertEquals("Received payload is equal to sent payload.",message,MESSAGE_PAYLOAD_1);resolver.resolve()});outerXpc.connect(function(){assertTrue("XPC over direct channel is connected",outerXpc.isConnected());messageIsSync=!0;outerXpc.send(ECHO_SERVICE_NAME,MESSAGE_PAYLOAD_1);messageIsSync=!1});window.iframeLoadHandler=function(){var cfg=getConfiguration(CrossPageChannelRole.INNER);cfg[CfgFields.DIRECT_TRANSPORT_SYNC_MODE]=!0;peerIframe.contentWindow.instantiateChannel(cfg)};peerIframe.src="testdata/inner_peer.html";return resolver.promise}