goog.provide("goog.result.DeferredAdaptor");goog.require("goog.async.Deferred");goog.require("goog.result");goog.require("goog.result.Result");goog.result.DeferredAdaptor=function(result){goog.result.DeferredAdaptor.base(this,"constructor");goog.result.wait(result,function(result){if(this.hasFired()){return}if(result.getState()==goog.result.Result.State.SUCCESS){this.callback(result.getValue())}else if(result.getState()==goog.result.Result.State.ERROR){if(result.getError()instanceof goog.result.Result.CancelError){this.cancel()}else{this.errback(result.getError())}}},this)};goog.inherits(goog.result.DeferredAdaptor,goog.async.Deferred);