goog.provide("goog.soy.dataTest");goog.setTestOnly("goog.soy.dataTest");goog.require("goog.html.SafeHtml");goog.require("goog.soy.testHelper");goog.require("goog.testing.jsunit");function testToSafeHtml(){var html;html=example.unsanitizedTextTemplate().toSafeHtml();assertEquals("I &lt;3 Puppies &amp; Kittens",goog.html.SafeHtml.unwrap(html));html=example.sanitizedHtmlTemplate().toSafeHtml();assertEquals("Hello <b>World</b>",goog.html.SafeHtml.unwrap(html))}