goog.provide("goog.storage.mechanism.ErrorHandlingMechanism");goog.require("goog.storage.mechanism.Mechanism");goog.storage.mechanism.ErrorHandlingMechanism=function(mechanism,errorHandler){goog.storage.mechanism.ErrorHandlingMechanism.base(this,"constructor");this.mechanism_=mechanism;this.errorHandler_=errorHandler};goog.inherits(goog.storage.mechanism.ErrorHandlingMechanism,goog.storage.mechanism.Mechanism);goog.storage.mechanism.ErrorHandlingMechanism.Operation={SET:"set",GET:"get",REMOVE:"remove"};goog.storage.mechanism.ErrorHandlingMechanism.ErrorHandler;goog.storage.mechanism.ErrorHandlingMechanism.prototype.set=function(key,value){try{this.mechanism_.set(key,value)}catch(e){this.errorHandler_(e,goog.storage.mechanism.ErrorHandlingMechanism.Operation.SET,key,value)}};goog.storage.mechanism.ErrorHandlingMechanism.prototype.get=function(key){try{return this.mechanism_.get(key)}catch(e){this.errorHandler_(e,goog.storage.mechanism.ErrorHandlingMechanism.Operation.GET,key);return null}};goog.storage.mechanism.ErrorHandlingMechanism.prototype.remove=function(key){try{this.mechanism_.remove(key)}catch(e){this.errorHandler_(e,goog.storage.mechanism.ErrorHandlingMechanism.Operation.REMOVE,key)}};