goog.provide("goog.storage.mechanism.HTML5LocalStorageTest");goog.setTestOnly("goog.storage.mechanism.HTML5LocalStorageTest");goog.require("goog.storage.mechanism.HTML5LocalStorage");goog.require("goog.storage.mechanism.mechanismSeparationTester");goog.require("goog.storage.mechanism.mechanismSharingTester");goog.require("goog.storage.mechanism.mechanismTestDefinition");goog.require("goog.testing.jsunit");goog.require("goog.userAgent");function setUp(){var localStorage=new goog.storage.mechanism.HTML5LocalStorage;if(localStorage.isAvailable()){mechanism=localStorage;minimumQuota=1024*(1024*2);mechanism_shared=new goog.storage.mechanism.HTML5LocalStorage}}function tearDown(){if(!!mechanism){mechanism.clear();mechanism=null}if(!!mechanism_shared){mechanism_shared.clear();mechanism_shared=null}}function testAvailability(){if(goog.userAgent.WEBKIT&&goog.userAgent.isVersionOrHigher("532.5")||goog.userAgent.GECKO&&goog.userAgent.isVersionOrHigher("1.9.1")||goog.userAgent.IE&&goog.userAgent.isVersionOrHigher("8")){assertNotNull(mechanism);assertTrue(mechanism.isAvailable());assertNotNull(mechanism_shared);assertTrue(mechanism_shared.isAvailable())}}